export default function Button({ children, halfWidth = false }) {
  return (
    <button
      type="button"
      className={`text-[2rem] text-white bg-indigo-400 hover:bg-blue-200 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm mr-2 last:mr-0 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800 h-[6rem] ${!halfWidth ? "w-full" : "w-[205%]"}`}
    >
      {children}
    </button>
  );
}
