import "./App.css";
import InputDisplay from "./components/input-display";
import Button from "./components/button";
import Row from "./components/row";

function App() {
  const btnValues = [
    ["C", "+-", "%", "/"],
    [7, 8, 9, "X"],
    [4, 5, 6, "-"],
    [1, 2, 3, "+"],
    [0, ".", "="],
  ];

  const total = 0;
  const input = []; // input stack

  const inputClick = () => {
    console.log("click");
  };

  const renderButtons = () => {
    let row = [];
    for (let i = 0; i < btnValues.length; i++) {
      row.push(
        <Row key={i}>
          {btnValues[i].forEach((btnValue) => {
            return <Button inputClick={inputClick}>{btnValue}</Button>;
          })}
        </Row>
      );
    }
    return row;
  };

  const renderBtnRow = (rowIdx) => {
    return (
      <Row key={rowIdx}>
        {btnValues[rowIdx].forEach((btnValue) => {
          return <Button inputClick={inputClick}>{btnValue}</Button>;
        })}
      </Row>
    );
  };

  return (
    <div className="flex justify-center">
      <div className="w-[34rem] h-[41rem] border-double border-2 border-indigo-700 rounded px-2 py-2 [&>*]:py-1 bg-indigo-200">
        <InputDisplay total={total}></InputDisplay>
        {btnValues.forEach((btnValue, index) => renderBtnRow(index))}
      </div>
    </div>
  );
}

export default App;
